import React from "react";
import { Select } from "@material-ui/core";
import { StyledSelectFormControl, StyledDropdownIcon } from "./styles";
import { StyledMenuItem } from "../FormInputs";
import styled from "styled-components";
import _ from "lodash";
import COLORS from "../../../assets/Colors";

const CustomSelect = ({
	label,
	name,
	value = [],
	onChange,
	disabled,
	menuItemValues,
	multiple = false,
	selectedItems = [],
	white = false,
	helperText,
	showId = false,
	...props
}) => {
	function renderValue(selectValue) {
		if (multiple) {
			const selectedKeys = menuItemValues
				.filter((menuItemValue) => selectValue?.includes(menuItemValue?.value))
				.map((menuItemValues) => menuItemValues?.label);
			console.log("selectedKeys", selectedKeys);
			return selectedKeys?.join(", ");
		} else {
			const selectedKey = menuItemValues.find(
				(menuItemValue) => menuItemValue.value === selectValue
			);
			console.log("selectedKey", selectedKey);
			return _.trim(selectValue) === "" ? (
				<PlaceHolderText>{selectedKey?.label}</PlaceHolderText>
			) : selectedKey?.label === "Agreements" ? (
				<b style={{ color: COLORS.LOGOUT_RED }}>{selectedKey?.value}</b>
			) : selectedKey?.value === "Notices, Claims & Counterclaims" ? (
				<b style={{ color: COLORS.BOX_BLUE }}>{selectedKey?.value}</b>
			) : selectedKey?.value === "Evidence & Arguments" ? (
				<b style={{ color: COLORS.BOX_INDIGO }}>{selectedKey?.value}</b>
			) : selectedKey?.value === "Petitions" ? (
				<b style={{ color: COLORS.BOX_DEEPPINK }}>{selectedKey?.value}</b>
			) : selectedKey?.value === "Details of Proceedings" ? (
				<b style={{ color: COLORS.BOX_DARKMAGENTA }}>{selectedKey?.value}</b>
			) : selectedKey?.value === "Orders & Minutes" ? (
				<b style={{ color: COLORS.BOX_DARKGREEN }}>{selectedKey?.value}</b>
			) : (
				selectedKey?.value
			);
		}
	}

	console.log(menuItemValues);

	return (
		<StyledSelectFormControl
			className={white ? "input-white" : ""}
			variant="outlined"
		>
			<Select
				{...{ multiple, name, value, onChange, renderValue, disabled }}
				{...props}
				IconComponent={() => (
					<StyledDropdownIcon
						alt="select"
						src={require("../../../assets/images/dropdownIcon.svg")}
					/>
				)}
			>
				{menuItemValues.map((item, idx) => (
					<StyledMenuItem key={idx} value={item.value} disabled={item.disabled}>
						{item.label === "Agreements" ? (
							<b style={{ color: COLORS.LOGOUT_RED }}>{item.label}</b>
						) : item.label === "Notices, Claims & Counterclaims" ? (
							<b style={{ color: COLORS.BOX_BLUE }}>{item.label}</b>
						) : item.label === "Evidence & Arguments" ? (
							<b style={{ color: COLORS.BOX_INDIGO }}>{item.label}</b>
						) : item.label === "Petitions" ? (
							<b style={{ color: COLORS.BOX_DEEPPINK }}>{item.label}</b>
						) : item.label === "Details of Proceedings" ? (
							<b style={{ color: COLORS.BOX_DARKMAGENTA }}>{item.label}</b>
						) : item.label === "Orders & Minutes" ? (
							<b style={{ color: COLORS.BOX_DARKGREEN }}>{item.label}</b>
						) : (
							item.label
						)}
					</StyledMenuItem>
				))}
			</Select>
			{helperText && <ErrorLabel>{helperText}</ErrorLabel>}
		</StyledSelectFormControl>
	);
};

export default CustomSelect;

const PlaceHolderText = styled.div`
	opacity: 0.5;
`;

const ErrorLabel = styled.div`
	font-size: 10px;
	font-family: openSans-SemiBold, sans-serif;
	color: #f44336;
	margin: 5px 0px 0px 5px;
`;
