import React from "react";
import {
  FormWrapper,
  H2,
  Form,
  FormElWrapper,
  ButtonWrapper,
  Copyrights,
} from "./Styles";
import { Formik } from "formik";
import FormField from "../../common/FormField/FormField";
import { PrimaryCTAButton } from "../../common/Buttons";
import { DiscussFormValidationSchema } from "../../../helpers/validationSchema";
import { useSnackbar } from "notistack";
import { getErrorMessage } from "../../../helpers/functions";
import useLoader from "../../../hooks/useLoader";
import CaseService from "../../../services/CaseService";
import theme from "../../../assets/theme";

const Index = () => {
  const { enqueueSnackbar } = useSnackbar();
  const { setLoader } = useLoader();

  const inputs = [
    {
      type: "text",
      name: "name",
      label: "Full Name",
      required: true,
    },
    {
      type: "text",
      name: "email",
      label: "Email",
      required: true,
    },
    {
      type: "text",
      name: "mobile",
      label: "Phone Number",
      required: true,
    },
  ];

  async function onFormSubmit(values, { resetForm }) {
    setLoader({ state: true, message: "Loading..." });
    try {
      const res = await CaseService.scheduleMeeting(values);
      if (res?.message) {
        enqueueSnackbar(res?.message, {
          variant: "success",
        });
        resetForm();
      }
    } catch (error) {
      const message = getErrorMessage(error);
      enqueueSnackbar(message, {
        variant: "error",
      });
    } finally {
      setLoader({ state: false });
    }
  }

  return (
    <FormWrapper>
      <H2 style={{ marginTop: 40, marginBottom: 50 }}>Early Case Evaluation</H2>
      <Formik
        initialValues={{
          name: "",
          email: "",
          mobile: "",
        }}
        onSubmit={onFormSubmit}
        validationSchema={DiscussFormValidationSchema}
        validateOnBlur
        validateOnChange
      >
        {({
          values,
          handleChange,
          errors,
          handleSubmit,
          touched,
          handleBlur,
          setFieldValue,
        }) => (
          <Form>
            {inputs.map((input, key) => (
              <FormElWrapper key={key}>
                <FormField
                  {...{
                    input,
                    key,
                    values,
                    handleChange,
                    errors,
                    handleSubmit,
                    touched,
                    handleBlur,
                    setFieldValue,
                  }}
                />
              </FormElWrapper>
            ))}
            <ButtonWrapper>
              <PrimaryCTAButton
                type="submit"
                onClick={handleSubmit}
                style={{
                  width: "100%",
                  marginLeft: "unset",
                  marginTop: 35,
                  font: theme.fonts.primaryFontRegular,
                }}
              >
                Submit
              </PrimaryCTAButton>
              <p style={{ margin: "10px", color: "grey", textAlign: "center" }}>
                We will have one of our case managers discuss with you the
                various options to effectively pursue your case.
              </p>
            </ButtonWrapper>
          </Form>
        )}
      </Formik>
    </FormWrapper>
  );
};
export default Index;
