import React, { useEffect } from 'react';
import { scroller } from 'react-scroll';

import { Header } from '../../internal/Header/Header';
import Footer from '../LandingPage/Footer';
import LongTraining from './LongTraining';
import ShortTraining from './ShortTraining';
import WorldofMediation from "./WorldOfMediation";

function Training(props) {
    useEffect(()=> {
        scroller.scrollTo(0, {
            duration: 500,
            offset: -70,
            smooth: true,
          })
    },[])
    return (
        <div>
            <Header >
            {(Number(props.id)===40)?<LongTraining/>:(Number(props.id)===60)?<WorldofMediation/>:<ShortTraining/>}
            </Header>
            <Footer/>
        </div>
    );
}

export default Training;
