import React, { useEffect, useState } from "react";
import styled, { css } from "styled-components";
import COLORS from "../../../assets/Colors";
import theme from "../../../assets/theme";
import _ from "lodash";
import { Tooltip } from "@material-ui/core";

const Index = ({
	items = [],
	selectedIndex,
	isMediation = false,
	resolutionKind,
	kind
}) => {
	const [caseCycle, setCaseCycle] = useState([]);

	useEffect(() => {
		let duplicateList = [];
		// eslint-disable-next-line array-callback-return
		let cycleList = items.filter((item) => {
			if (!duplicateList.some((el) => el === item.index)) {
				duplicateList.push(item.index);
				return item;
			}
		});
		cycleList = cycleList.map((item) => {
			if (isMediation) {
				if (item?.label === "Negotiation Ongoing") {
					return { ...item, label: `${_.capitalize(resolutionKind)} Ongoing` };
				} else if (item?.label === "Negotiation Concluded") {
					return {
						...item,
						label: `${_.capitalize(resolutionKind)} Concluded`
					};
				} else if (
					item?.label === "Agreement" &&
					resolutionKind === "arbitration"
				) {
					return {
						...item,
						label: "Final Award"
					};
				} else {
					return item;
				}
			} else {
				return item;
			}
		});
		setCaseCycle(cycleList);
	}, [items, isMediation, resolutionKind]);

	return (
		<LifecycleWrapper>
			<BigScreen>
				{caseCycle.map((item, index) => (
					<LifecycleBorder
						isFirst={index === 0}
						selected={selectedIndex > index}
						key={index}
					>
						<Lifecycle isFirst={index === 0} selected={selectedIndex > index}>
							{console.log("selectedIndex", selectedIndex)}
							{!kind && resolutionKind === "negotiation" ? (
								<Tooltip
									title={
										item.label === "Awaiting Respondent onboarding"
											? "Your Respondent(s) has been contacted with the case details provided by you. You will receive an intimation on email when the Respondent joins JustAct to resolve the dispute."
											: item.label === "Case Accepted/Waiting"
											? "Your Respondent(s) has accepted the case. We will be in touch with you to when they respond to your claim to begin negotiation."
											: item.label === "Negotiation Ongoing"
											? "You are in the process of negotiations with your counterparty"
											: item.label === "Negotiation Concluded"
											? "Your negotiation has concluded. If the negotiation was successful, we will be preparing a Settlement Agreement for you."
											: "You Settlement Agreement is ready. You may need to pay the Agreement Fee from the Case Cart to collect your Agreement."
									}
									placement="top"
								>
									<TextContainer>{item.label}</TextContainer>
								</Tooltip>
							) : !kind && resolutionKind === "mediation" ? (
								<Tooltip
									title={
										item.label === "Awaiting Respondent onboarding"
											? "Your Respondent has been contacted with the case details provided by you. You will receive an intimation on email when the Respondent joins JustAct to resolve the dispute."
											: item.label === "Case Accepted/Waiting"
											? "Your Respondent(s) has accepted the case. Your case manager will be in touch with you to start the mediation proceedings."
											: item.label === "Mediation Ongoing"
											? "You are in the process of mediation with your counterparty with the help of your mediator."
											: item.label === "Mediation Concluded"
											? "Your mediation has concluded. If the mediation was successful, your case manager will be in touch with you complete the Settlement Agreement formalities."
											: "You Settlement Agreement is ready. You may need to pay the Agreement Fee from the case cart to collect your Agreement."
									}
									placement="top"
								>
									<TextContainer style={{ cursor: "pointer" }}>
										{item.label}
									</TextContainer>
								</Tooltip>
							) : !kind && resolutionKind === "arbitration" ? (
								<Tooltip
									title={
										item.label === "Awaiting Respondent onboarding"
											? "Your Respondent has been contacted with the case details provided by you. You will receive an intimation on email when the Respondent joins JustAct to resolve the dispute."
											: item.label === "Case Accepted/Waiting"
											? "Your Respondent(s) has accepted the case. Your case manager will be in touch with you to start the Arbitration hearings."
											: item.label === "Arbitration Ongoing"
											? "You are going through the process of Arbitration hearings. "
											: item.label === "Arbitration Concluded"
											? "Your Arbitration has concluded. The Arbitral Award will be available for you to download shortly."
											: "You arbitral award has been passed. You may download it for your reference."
									}
									placement="top"
								>
									<TextContainer style={{ cursor: "pointer" }}>
										{item.label}
									</TextContainer>
								</Tooltip>
							) : kind && resolutionKind === "negotiation" ? (
								<Tooltip
									title={
										item.label === "Negotiation Ongoing"
											? "You are in the process of negotiations with your counterparty"
											: item.label === "Negotiation Concluded"
											? "Your negotiation has concluded. If the negotiation was successful, we will be preparing a Settlement Agreement for you."
											: "You Settlement Agreement is ready. You may need to pay the Agreement Fee from the Case Cart to collect your Agreement."
									}
									placement="top"
								>
									<TextContainer>{item.label}</TextContainer>
								</Tooltip>
							) : kind && resolutionKind === "mediation" ? (
								<Tooltip
									title={
										item.label === "Mediation Ongoing"
											? "You are in the process of mediation with your counterparty with the help of your mediator."
											: item.label === "Mediation Concluded"
											? "Your mediation has concluded. If the mediation was successful, your case manager will be in touch with you complete the Settlement Agreement formalities."
											: "You Settlement Agreement is ready. You may need to pay the Agreement Fee from the case cart to collect your Agreement."
									}
									placement="top"
								>
									<TextContainer>{item.label}</TextContainer>
								</Tooltip>
							) : (
								<Tooltip
									title={
										item.label === "Arbitration Ongoing"
											? "You are going through the process of Arbitration hearings."
											: item.label === "Arbitration Concluded"
											? "Your Arbitration has concluded. The Arbitral Award will be available for you to download shortly."
											: "You arbitral award has been passed. You may download it for your reference."
									}
									placement="top"
								>
									<TextContainer>{item.label}</TextContainer>
								</Tooltip>
							)}
						</Lifecycle>
					</LifecycleBorder>
				))}
			</BigScreen>
			<SmallScreen>
				<Row>
					{caseCycle.map((item, index) => (
						<MiniContainer {...{ selectedIndex, index, caseCycle }}>
							<div>
								<Border
									isFirst={index === 0}
									selected={selectedIndex > index}
									key={index}
								>
									<Cycle isFirst={index === 0} selected={selectedIndex > index}>
										<Text>{item.index}</Text>
									</Cycle>
								</Border>
							</div>
							{caseCycle?.length !== index + 1 ? (
								<div className="border"></div>
							) : null}
						</MiniContainer>
					))}
				</Row>
				{caseCycle
					.filter((item) => {
						return selectedIndex === item.index;
					})
					.map((item) => {
						return (
							<Text
								style={{
									color: `${COLORS.COLOR_DARK}`,
									textTransform: "uppercase"
								}}
							>
								{item.label}
							</Text>
						);
					})}
			</SmallScreen>
		</LifecycleWrapper>
	);
};

export default Index;

const SmallScreen = styled.div`
	width: 100%;
	display: flex;
	flex-direction: column;
	@media ${theme?.breakpoints?.lg_up} {
		display: none;
	}
`;

const MiniContainer = styled.div`
	width: 100%;
	display: inline-flex;
	align-items: center;
	justify-content: space-between;
	${({ caseCycle, index }) =>
		caseCycle?.length === index + 1 &&
		css`
			width: auto;
		`}
	& .border {
		width: 100%;
		height: 3px;
		background: #acb1c2;
		${({ selectedIndex, index }) =>
			selectedIndex > index + 1 &&
			css`
				background-color: ${COLORS.COLOR_DARK};
			`}
	}
`;

const Row = styled.div`
	width: 100%;
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin: 19px 0px 30px 0;
	@media ${theme?.breakpoints?.sm_up} {
		max-width: 600px;
	}
`;
const BigScreen = styled.div`
	display: none;
	@media ${theme?.breakpoints?.lg_up} {
		display: flex;
	}
`;

const Border = styled.div`
	width: 35px;
	height: 35px;
	border-radius: 50%;
	background-color: #acb1c2;
	${({ selected }) =>
		selected &&
		css`
			background-color: ${COLORS.COLOR_DARK};
		`}
`;

const Cycle = styled.div`
	height: 100%;
`;

const Text = styled.div`
	font-family: ${theme.fonts.primaryFontBold};
	font-size: 12px;
	height: 100%;
	display: flex;
	justify-content: center;
	align-items: center;
	line-height: 1.6;
	color: ${COLORS.PRIMARY_WHITE};
	@media ${theme?.breakpoints?.sm_up} {
		max-width: 600px;
	}
`;

const LifecycleWrapper = styled.div`
	margin: 5px 0;
	display: flex;
`;
const LifecycleBorder = styled.div`
	width: 160px;
	height: 60px;
	display: flex;
	justify-content: center;
	align-items: center;
