import { CircularProgress } from "@material-ui/core";
import React from "react";
import styled from "styled-components";
import COLORS from "../../../assets/Colors";
import theme from "../../../assets/theme";
import useSettings from "../../../hooks/useSettings";
import { Header } from "../../internal/Header/Header";
import Footer from "../LandingPage/Footer";

function TermsAndCondition(props) {
  const { setting, settingsLoading } = useSettings();
  return (
    <div>
      <Header />
      <H2 style={{ marginTop: "20px" }}>Terms and Conditions</H2>
      {settingsLoading ? (
        <Loader>
          <CircularProgress size={60} thickness={3} />
        </Loader>
      ) : (
        <Container>
          {setting?.tnc?.map((terms, index) => (
            <div className="ql-editor" key={index}>
              <Padding>
                <SubHeading>{terms?.question}</SubHeading>
                <div dangerouslySetInnerHTML={{ __html: terms?.answer }}></div>
              </Padding>
            </div>
          ))}
        </Container>
      )}
      <Footer />
    </div>
  );
}

export default TermsAndCondition;

const H2 = styled.h2`
  font-family: ${theme.fonts.primaryFontBold};
  color: ${COLORS.COLOR_DARK};
  font-size: 30px;
  margin-bottom: 32px;
  cursor: default;
  padding: 0 20px;
  text-align: center;
  @media (max-width: 1024px) {
    font-size: 24px;
    text-align: center;
  }
`;

const Container = styled.div`
  width: 100%;
  text-align: justify;
  margin-bottom: 20px;
  font-family: ${theme.fonts.primaryFontRegular};
  font-size: 16px;
  @media ${theme?.breakpoints?.sm_up} {
    width: 70%;
    margin: auto;
  }
`;
const SubHeading = styled.div`
  font-size: 20px;
  margin: 10px 0;
  color: ${COLORS.BTN_GREEN};
`;

const Padding = styled.div`
  margin: 0 15px;
  @media ${theme?.breakpoints?.sm_up} {
    margin: 0;
  }
`;

const UnorderedList = styled.ol`
  padding-left: 20px;
  margin: 10px 0;
`;

const Loader = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  height: 500px;
`;
