import React, { useState } from "react"
import styled from "styled-components"
import theme from "../../../assets/theme"
import COLORS from "../../../assets/Colors"
import Drawer from "../../common/Drawer"
import { Heading } from "../../../styles/component/style"
import CustomTable from "../../common/CustomTable/CustomTable"
import CaseService from "../../../services/CaseService"
import moment from "moment"
import queryString from "query-string"
import {
  downloadWithDifferenFileName,
  getErrorMessage,
  parseTimeStamp,
} from "../../../helpers/functions"
import { useSnackbar } from "notistack"
import { makeStyles, Tooltip } from "@material-ui/core"
import Accordion from "@material-ui/core/ExpansionPanel"
import AccordionSummary from "@material-ui/core/ExpansionPanelSummary"
import AccordionDetails from "@material-ui/core/ExpansionPanelDetails"
import ArbitrationDrawer from "./ArbitrationDrawer"
import ExpandMoreIcon from "@material-ui/icons/ExpandMore"
import HttpsIcon from "@material-ui/icons/Https"
import PublicIcon from "@material-ui/icons/Public"
import { useMediaQuery } from "@material-ui/core"
import { useTheme } from "@material-ui/core/styles"
import DrawerRecording from "../ViewRecording/ViewRecording"
import HearingBundles from "../HearingBundles/index"
import DocTypeChnage from "../DocumentTypeChange/DocTypeChange"
import HttpsLockOpen from "@material-ui/icons/LockOpen"
import DocVisibilityTypeChange from "../../internal/DocVisibilityTypeChange/index"

const docTypes = [
  { ownerType: "claimant", title: "Claimant Documents" },
  { ownerType: "respondent", title: "Respondent Documents" },
  { ownerType: "common", title: "Neutral's Documents" },
]

async function getCaseDocument(id, query) {
  try {
    const response = await CaseService.getcaseDocuments(id, query)
    return { ...response }
  } catch (error) {
    throw error
  }
}

const Index = ({ state, setState, id, type, agentRole }) => {
  return (
    <Drawer {...{ state }} setState={() => {}} label="Drawer">
      <Table {...{ setState, id, type, agentRole }} />
    </Drawer>
  )
}

export default Index

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    backgroundColor: "#f9f9f9",
    border: "solid 0.5px #e1e3ee",
    boxShadow: "none",
    margin: "16px 0px 0px !important",
    "& .MuiIconButton-root": {
      padding: 0,
      marginRight: 15,
      backgroundColor: "#fff",
    },
    "& .MuiSvgIcon-root": {
      fill: "#00838c",
    },
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}))

const Table = ({ setState, id, type, agentRole }) => {
  return (
    <div>
      <Header>
        <Heading style={{ marginBottom: "unset" }}>Case Documents</Heading>
        <ImageWrapper>
          <img
            src={require("../../../assets/images/closeModal.svg")}
            onClick={() => {
              setState(false)
            }}
            alt="close"
          />
        </ImageWrapper>
      </Header>
      <AccordionContainer>
        {docTypes
          .filter((item) =>
            type === "negotiation" ? item.ownerType !== "mediator" : true
          )
          .map((item, index) => (
            <div key={index}>
              <TableWithAccordion {...item} {...{ id, setState, agentRole }} />
            </div>
          ))}
        {type === "arbitration" ? (
          <ArbitrationDrawer
            title="Template Agreements and Awards"
            {...{ id, setState }}
          />
        ) : (
          ""
        )}
        {id ? <HearingBundles title="Hearing Bundles" {...{ id }} /> : ""}
        {type !== "negotiation" ? (
          <DrawerRecording title="View Recordings" {...{ id, setState }} />
        ) : (
          ""
        )}
      </AccordionContainer>
    </div>
  )
}

