      cellStyle: {
        maxWidth: 200,
        width: 200,
        padding: `12px 16px`,
      },
    },
  ]

  const data = (query) =>
    new Promise((resolve) => {
      let payload = {
        page: query.page + 1,
        perPage: 10,
      }
      if (ownerType) {
        if (ownerType === "claimant") {
          payload.claimantView = true
        } else if (ownerType === "respondent") {
          payload.respondentView = true
        } else {
          payload.commonView = true
        }
      }
      const stringParams = `?${queryString.stringify(payload)}`
      getCaseDocument(id, stringParams)
        .then(async (result) => {
          if (result) {
            resolve({
              data: result.data,
            })
            setResult(result)
          }
        })
        .catch((error) => {
          enqueueSnackbar(getErrorMessage(error), {
            variant: "error",
          })
          setResult({
            data: [],
            lastPage: 1,
            page: 1,
            perPage: 10,
            total: 0,
          })
          resolve({
            data: [],
            page: 0,
            total: 0,
          })
        })
