import React from "react";
import ClickAwayListener from "@material-ui/core/ClickAwayListener";
import Grow from "@material-ui/core/Grow";
import Paper from "@material-ui/core/Paper";
import Popper from "@material-ui/core/Popper";
import MenuItem from "@material-ui/core/MenuItem";
import MenuList from "@material-ui/core/MenuList";
import { makeStyles } from "@material-ui/core/styles";
import { navigate } from "@reach/router";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { scroller } from "react-scroll";
import theme from "../../../assets/theme";

const useStyles = makeStyles(theme => ({
  root: {
    display: "flex"
  },
  paper: {
    marginRight: theme.spacing(2)
  },
  arrow: {
    fontSize: "small"
  }
}));

export default function DropDownMenu(props) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const anchorRef = React.useRef(null);

  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  };

  const handleClose = event => {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return;
    }
    console.log(event.target);
    setOpen(false);
  };

  function handleListKeyDown(event) {
    if (event.key === "Tab") {
      event.preventDefault();
      setOpen(false);
    }
  }

  // return focus to the button when we transitioned from !open -> open
  const prevOpen = React.useRef(open);
  React.useEffect(() => {
    if (prevOpen.current === true && open === false) {
      anchorRef.current.focus();
    }

    prevOpen.current = open;
  }, [open]);

  return (
    <div className={classes.root}>
      <div>
        <div
          ref={anchorRef}
          aria-controls={open ? "menu-list-grow" : undefined}
          aria-haspopup="true"
          onClick={handleToggle}
        >
          {props.title} <ExpandMoreIcon className={classes.arrow} />
        </div>
        <Popper
          open={open}
          anchorEl={anchorRef.current}
          role={undefined}
          transition
          disablePortal
        >
          {({ TransitionProps, placement }) => (
            <Grow
              {...TransitionProps}
              style={{
                transformOrigin:
                  placement === "bottom" ? "center top" : "center bottom"
              }}
            >
              <Paper>
                <ClickAwayListener onClickAway={handleClose}>
                  <MenuList
                    autoFocusItem={open}
                    id="menu-list-grow"
                    onKeyDown={handleListKeyDown}
                  >
                    {props.options.map(option => (
                      <MenuItem
                        key={option.name}
                        style={{
                          fontFamily: theme.fonts.primaryFontRegular,
                          fontSize: "14px"
                        }}
                        onClick={() => {
                          option.link === process.env.REACT_APP_NEUTRAL_BASE_URL
                            ? `${((
                                <Helmet>
                                  <meta
                                    http-equiv="Content-Type"
                                    content="text/html; charset=utf-8"
                                  />
                                  <title>
                                    JustAct Neutral Portal - Online Dispute
                                    Resolution in India
                                  </title>
                                  <meta
                                    name="description"
                                    content="Welcome to JustAct Neutral Portal."
                                  />
                                  <meta
                                    name="keywords"
                                    content="JustAct Neutral Portal, Online Dispute Resolution in India, Dispute Resolution Online, Alternative Dispute Resolution, JustAct"
                                  />
                                </Helmet>
                              ),
                              window.open(option.link, "_blank"))}`
                            : navigate(option.link);
                          setOpen(false);
                          scroller.scrollTo(
                            option.section ? option.section : 0,
                            {
                              duration: 500,
                              offset: -70,
                              smooth: true
                            }
                          );
                        }}
                      >
                        {option.name}
                      </MenuItem>
                    ))}
                  </MenuList>
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </div>
    </div>
  );
}
