import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Button from "@material-ui/core/Button";
import MenuIcon from "@material-ui/icons/Menu";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import {
  ExpansionPanel,
  ExpansionPanelDetails,
  ExpansionPanelSummary,
} from "@material-ui/core";
import { navigate } from "@reach/router";
import { H2 } from "./Styles";
import styled from "styled-components";
import theme from "../../../assets/theme";
import COLORS from "../../../assets/Colors";

const useStyles = makeStyles((theme) => ({
  root: {
    "&.MuiButton-root": {
      minWidth: "auto",
    },
    [theme.breakpoints.up(640)]: {
      "&.MuiButton-root": {
        minWidth: "64px",
      },
    },
  },
  list: {
    overflow: "auto",
  },
  fullList: {
    width: "unset",
  },
  padder: {
    paddingTop: "74px",
  },
  sidePadder: {
    paddingLeft: "2%",
    paddingRight: "5%",
    margin: 0,
    boxShadow: "none",
    borderBottom: "1px solid #e8e8e8",
  },
  listPadder: {
    padding: 0,
  },
}));

export default function TemporaryDrawer() {
  const classes = useStyles();
  const [state, setState] = React.useState(false);

  const toggleDrawer = (open) => {
    setState(open);
  };

  const list = () => (
    <div
      className={clsx(classes.list, classes.fullList, classes.padder)}
      role="presentation"
      //   onClick={toggleDrawer(false)}
      onKeyDown={() => toggleDrawer(false)}
    >
      <ExpansionPanel className={classes.sidePadder}>
        <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
          <H2>Resources</H2>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails style={{ padding: 0 }}>
          <MenuList>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/resources/blogs");
              }}
            >
              Blog
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/resources/webinar-videos");
              }}
            >
              Webinar
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/resources/model-clauses");
              }}
            >
              Model Clauses
            </MenuItem>
          </MenuList>
        </ExpansionPanelDetails>
      </ExpansionPanel>

      <ExpansionPanel className={classes.sidePadder}>
        <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
          <H2>Neutrals</H2>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails style={{ padding: 0 }}>
          <MenuList>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/neutral/arbitrators");
              }}
            >
              Arbitrators
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/neutral/mediators");
              }}
            >
              Mediators
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                window.open(process.env.REACT_APP_NEUTRAL_BASE_URL, "_blank");
              } }
            >
              Login as Neutral
            </MenuItem>
          </MenuList>
        </ExpansionPanelDetails>
      </ExpansionPanel>

      <ExpansionPanel className={classes.sidePadder}>
        <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
          <H2>Training</H2>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails style={{ padding: 0 }}>
          <MenuList>
            <MenuItem
              onClick={() => {
                navigate("/training/corporate-short-programme");
                toggleDrawer(false);
              }}
            >
              Corporate Short Training
            </MenuItem>
            <MenuItem
              onClick={() => {
                navigate("/training/mediation-training-programme");
                toggleDrawer(false);
              }}
            >
              Mediator Training Programme
            </MenuItem>
            <MenuItem
              onClick={() => {
                navigate("/training/world-of-mediation");
                toggleDrawer(false);
              }}
            >
              World of Mediation
            </MenuItem>
          </MenuList>
        </ExpansionPanelDetails>
      </ExpansionPanel>
      <ExpansionPanel className={classes.sidePadder}>
        <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
          <H2>About Us</H2>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails style={{ padding: 0 }}>
          <MenuList>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/about-us");
              }}
            >
              About JustAct
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#our-advantages");
              }}
            >
              Our Advantages
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#how-it-works");
              }}
            >
              How JustAct Works
            </MenuItem>
            <MenuItem
              onClick={() => {
                navigate("/aboutus#our-values");
                toggleDrawer(false);
              }}
            >
              Our Values
            </MenuItem>
            {/* <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#founders");
              }}
            >
              Founders
            </MenuItem> */}
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#board-of-directors");
              }}
            >
              Board of Directors
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#management-team");
              }}
            >
              Management Team
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/aboutus#partnerships");
              }}
            >
              Our Partners
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/terms-and-conditions");
              }}
            >
              Terms And Conditions
            </MenuItem>
            <MenuItem
              onClick={() => {
                toggleDrawer(false);
                navigate("/privacy-policy");
              }}
            >
              Privacy Policy
            </MenuItem>
          </MenuList>
        </ExpansionPanelDetails>
      </ExpansionPanel>

      {/* <MenuList>
        <MenuItem
          onClick={() => {
            navigate("/login");
            toggleDrawer(true);
          }}
        >
          <H2> Login</H2>
        </MenuItem>
        <MenuItem
          onClick={() => {
            navigate("/signup");
            toggleDrawer(true);
          }}
        >
          <H2> Sign Up</H2>
        </MenuItem>
      </MenuList> */}
    </div>
  );

  return (
    <div style={{ display: "flex" }}>
      <Container>
        <LoginDiv
          onClick={() => {
            navigate("/login");
            toggleDrawer(true);
          }}
        >
          <p> Login</p>
        </LoginDiv>
        <LoginDiv
          onClick={() => {
            navigate("/signup");
            toggleDrawer(true);
          }}
        >
          <p> Sign Up</p>
        </LoginDiv>
      </Container>
      {
        <React.Fragment key="top">
          <Button
            className={classes.root}
            onClick={() => (state ? toggleDrawer(false) : toggleDrawer(true))}
          >
            <MenuIcon />
          </Button>
          <Drawer
            anchor={"top"}
            open={state}
            onClose={() => toggleDrawer(false)}
            style={{ zIndex: "1" }}
          >
            {list()}
          </Drawer>
        </React.Fragment>
      }
    </div>
  );
}

const MenuList = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;
const MenuItem = styled.div`
  display: flex;
  margin: 5px 0;
  padding: 10px;
  height: 50px;
  width: 100%;
  font-family: ${theme.fonts.primaryFontRegular};
  cursor: pointer;
  border-bottom: 1px solid #e8e8e8;
  margin: auto 0px;
  align-items: center;
`;

const Container = styled.div`
  display: flex;
  align-items: center;
