import React, { useEffect } from "react";
import "./App.css";
import "./styles/main.scss";
import { BodySection, Layout } from "./styles/component/style";
import AppRouter from "./AppRoutes";
import Providers from "./providers/Providers";
import Loader from "./components/common/Loader/Loader";
import AlertWrapper from "./components/common/Alert/AlertWrapper";
import ReactGA from "react-ga";
import { globalHistory } from "@reach/router";
import FreshChat from "./components/common/FreshChat";

ReactGA.initialize("UA-214190369-1");
function App() {
  useEffect(() => {
    ReactGA.pageview(window.location.pathname + window.location.search);
    console.log(window.location);
    globalHistory.listen(({ location }) => {
      ReactGA.pageview(window.location.pathname + window.location.search);
      // or use the new gtag API
      console.log(location);
    });
  }, []);

  return (
    <Providers>

        <BodySection>
          <Loader />
          <AlertWrapper />
          <AppRouter />
        </BodySection>
        <FreshChat />

    </Providers>
  );
}

export default App;
