  display: flex;
  padding-left: 5px;
  flex-wrap: wrap;
  height: auto;
  padding: 10px 3.3px 10px 9px;
  border-radius: 10px;
  background-color: #f9f9f9;
  margin-bottom: 15px;
  position: relative;
`;

export const Delete = styled.div`
  font-family:${theme.fonts.primaryFontBold};
  font-size: 12px;
  color: ${COLORS.WARNING_COLOR};
  position absolute;
  bottom:0;
  right:0;
  margin-bottom:10px;
  margin-right:5px;
`;

export const MonetaryText = styled.div`
  font-family: ${theme.fonts.primaryFontSemiBold};
  font-size: 12px;
  color: ${COLORS.COLOR_DARK};
  margin-top: 14px;
  span {
    font-size: 16px;
  }

  @media ${theme?.breakpoints?.md_up} {
    align-self: flex-end;
    margin-left: 30px;
    margin-top: 17px;
  }
`;
