import { navigate } from "@reach/router";
import React from "react";
import styled from "styled-components";
import COLORS from "../../../assets/Colors";
import theme from "../../../assets/theme";
import { PrimaryOutlinedCTAButton } from "../../common/Buttons";
import { Content } from "../Resources/Styles";
import { H2 } from "./Styles";

function index(props) {
  return (
    <>
      <H2 style={{ textAlign: "center", marginTop: "40px" }}>Blogs</H2>
      <CardContainer>
        <Card>
          <Icon
            src={require("../../../assets/images/Trackling_Thumbnail.png")}
          />
          <CardHeading>
            Tackling pending cases in Indian <br/>Courts
            <NewIcon src={require("../../../assets/images/new-gif-icon.gif")} />
          </CardHeading>
          <CardBody>
            Every year, a minimum of five crore cases are filed across various
            courts of India. Even as some of these cases stay pending for over
            ten years, judges dispose...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/tackling-pending-cases-in-indian-courts")}
          >
            Read More
          </BtnStyle>
        </Card>

        <Card>
          <Icon src={require("../../../assets/images/JA Thumbnail.png")} />
          <CardHeading>Advantages of ODR</CardHeading>
          <CardBody>
            The world now bears witness to an unprecedented situation, with the
            pandemic and its conditions rendering aspects far from a sense of
            normal, even in the future This has affected the very way of working...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/advantages-of-ODR")}
          >
            Read More
          </BtnStyle>
        </Card>

        <Card>
          <Icon src={require("../../../assets/images/Thumbnail_S1129A.png")} />
          <CardHeading>
            Section 11 & 29A: Impeding or expediting the Arbitration process?{" "}
          </CardHeading>
          <CardBody>
            Through the present health catastrophe that the world knows as
            COVID-19, arbitration has been shown to adapt better than other
            dispute resolution methods...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/impeding-or-expediting-the-arbitration-process")}
          >
            Read More
          </BtnStyle>
        </Card>

        <Card>
          <Icon src={require("../../../assets/images/Thumbnail_Nitika.png")} />
          <CardHeading>
            Evaluating ADR for resolving women-related disputes (Part 3 of 3){" "}
            {/* <NewIcon src={require("../../../assets/images/NewIcon.png")} /> */}
          </CardHeading>
          <CardBody>
            Arbitration, mediation, or consultation exemplify Alternative
            Conflict Resolution (ADR) procedures; to resolve conflicts without
            resorting to litigation...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/evaluating-ADR-for-resolving-women-related-disputes")}
          >
            Read More
          </BtnStyle>
        </Card>

        {/* <Card>
          <Icon src={require("../../../assets/images/Thumbnail_Vismita.png")} />
          <CardHeading>
            Scope of Alternative Dispute Resolution for effectively resolving
            women- related disputes
          </CardHeading>
          <CardBody>
            Violence against women is a matter of grave concern. The immediate
            and long-term physical sexual, and mental consequences...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/ADR-and-its-applications-to-women-related-disputes")}
          >
            Read More
          </BtnStyle>
        </Card> */}

        {/* <Card>
          <Icon src={require("../../../assets/images/Thumbnail4.png")} />
          <CardHeading>
            Resolving women related disputes effectively
          </CardHeading>
          <CardBody>
            Women are victims of different social evils. They are subjected to
            problems at their homes, at the workplace, in society, etc...
          </CardBody>
          <BtnStyle
            onClick={() => navigate("/blog/resolving-women-related-disputes")}
          >
            Read More
          </BtnStyle>
        </Card> */}
      </CardContainer>
    </>
  );
}

export default index;

const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin: 40px;
  @media (max-width: 960px) {
    margin: 40px 0px;
  }
`;
const CardBody = styled.div`
  font-size: 16px;
  font-family: ${theme.fonts.primaryFontRegular};
  margin: 0 20px 15px;
  text-align: left;
  @media (max-width: 600px) {
    font-size: 14px;
  }
`;

export const NewIcon = styled.img`
  width: 33px;
  height: 30px;
  vertical-align: middle;
`;

export const Icon = styled.img`
  width: 100%;
  height: 200;
  object-fit: contain;
  margin-right: 10px;
  color: ${COLORS.COLOR_DARK};
`;

const CardHeading = styled.div`
  font-family: ${theme.fonts.primaryFontSemiBold};
  color: ${COLORS.COLOR_DARK};
  font-size: 18px;
  font-weight: 600;
  margin: 20px 20px 10px 20px;
  text-align: left;
`;
const Card = styled.div`
  flex-direction: column;
  justify-content: space-between;
  width: 400px;
  margin: 20px;
  background-color: #ffffff;
  border-radius: 1%;
  @media (max-width: 960px) {
    margin: 20px 0px;
    width: 80%;
  }
`;

const BtnStyle = styled.section`
  color: #00838c;
  display: flex;
  font-size: 16px;
  flex-direction: row;
  cursor: pointer;
  text-align: left;
  float: left;
  margin: 0 20px 20px 20px;
  font-family: ${theme.fonts.primaryFontBold};
`;
