import React, { useEffect } from "react";
import styled from "styled-components";
import theme from "../../../assets/theme";
import COLORS from "../../../assets/Colors";
import {
  H2,
  SubHeading,
  BlogImageWrap,
  PrimaryStyledButton,
  HeadWrap,
} from "./Styles";
import { Paper, responsiveFontSizes } from "@material-ui/core";
import { navigate } from "@reach/router";
import { makeStyles, useTheme } from "@material-ui/core/styles";

const useStyles = makeStyles((themes) => ({
  root: {
    backgroundColor: "#f9f9f9",
    padding: "20px 15px",
    margin: "20px 0",
    [themes.breakpoints.up(640)]: {
      padding: "20px",
      margin: "20px",
    },
  },
}));

function Blog(props) {
  const classes = useStyles();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  return (
    <>
      <HeadWrap
        style={{
          paddingTop: "40px",
        }}
      >
        Blogs
      </HeadWrap>
      <BodyWrap>
        <Paper className={classes.root}>
          <BlogImageWrap
            style={{
              overflow: "hidden",
              borderRadius: 8,
              border: "solid 0.3px #979797",
            }}
            src={require("../../../assets/images/Trackling_Thumbnail.png")}
            alt="Independent"
          />
          <TextBox>
            <SubHeading>
              Tackling pending cases in Indian Courts{" "}
              <NewIcon
                src={require("../../../assets/images/new-gif-icon.gif")}
              />
            </SubHeading>
