import React, { useState } from "react";
import theme from "../../../assets/theme";
import CaseService from "../../../services/CaseService";
import { getErrorMessage } from "../../../helpers/functions";
import { useSnackbar } from "notistack";
import CustomSelect from "../../common/CustomSelect/CustomSelectDoc";
import useLoader from "../../../hooks/useLoader";
import { caseDocumentType } from "../../../helpers/constants";
import styled from "styled-components";

const DocChangeType = ({ rowData, docName, setState, refreshDetails, setRefreshDetails }) => {
  const { enqueueSnackbar } = useSnackbar();
  const { setLoader } = useLoader();
  let docId = rowData?.id;
  let caseId = rowData?.entityId;
  const [docType, setDocType] = useState(docName);
  const { triggerNotify, setTriggerNotify } = useNotification();

  useEffect(() => {
    if (triggerNotify) {
      setRefreshDetails(true);
      setTriggerNotify(false);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [triggerNotify]);

  const changeDocType = async value => {
    try {
      setLoader({ state: true, message: "Change Document..." });
      const payload = {
        uploadType: value
      };
      const res = await CaseService.changeDocType(payload, caseId, docId);
      if (res) {
        enqueueSnackbar("Document Type Changed Successfully", {
          variant: "success"
        });
        setDocType(value);
        setRefreshDetails(true);
      }
    } catch (error) {
      const message = getErrorMessage(error);
      enqueueSnackbar(message, {
        variant: "error"
      });
    } finally {
      setLoader({ state: false });
      setState(false);
    }
    if (refreshDetails) {
      setRefreshDetails(false);
      changeDocType();
    }
  };

  return (
    <Container style={{ display: "flex", alignItems: "center" }}>
      <CustomSelect
        menuItemValues={caseDocumentType}
        name={docType}
        value={docType}
        disabled={docType === "Arbitration Notice" ? true : false}
        onChange={e => changeDocType(e.target.value)}
      />
    </Container>
  );
};

export default DocChangeType;

export const Container = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  @media ${theme?.breakpoints?.sm_up} {
    width: auto;
  }
`;
