import React, { useRef } from "react";
import Drawer from "@material-ui/core/Drawer";
import styled from "styled-components";
import COLORS from "../../../../assets/Colors";
import theme from "../../../../assets/theme";
import {
  Label,
  ButtonContainer,
  Value,
  ProfilePic,
  ProfilePicContainer,
} from "../styles";
import FormField from "../../../common/FormField/FormField";
import {
  PrimaryCTAButton,
  PrimaryOutlinedCTAButton,
} from "../../../common/Buttons";
import useUser from "../../../../hooks/useUser";
import useLoader from "../../../../hooks/useLoader";
import AWSService from "../../../../services/AWSService";
import { Formik } from "formik";
import { EditProfileSchema } from "../../../../helpers/validationSchema";
import { states } from "../../../../helpers/constants";
import AuthService from "../../../../services/AuthService";
import { useSnackbar } from "notistack";
import { getErrorMessage } from "../../../../helpers/functions";
import labels from "../../../../helpers/labels.json";

export default function ProfileModal({ modal, setModal }) {
  const { userObject } = useUser();
  const fileRef = useRef();
  const { setLoader } = useLoader();
  const { enqueueSnackbar } = useSnackbar();

  async function s3Upload(file, field, setFieldValue, setFieldError) {
    if (file.type.includes("image/")) {
      try {
        setLoader({ state: true, message: `Uploading file: ${file.name} ...` });
        const payload = {
          key: file.name,
        };
        const s3Response = await AWSService.getS3URL(payload);
        if (s3Response.url) {
          const res = await AWSService.uploadToS3(s3Response.url, file);
          setFieldValue(field, res);
          enqueueSnackbar("Successfully Uploaded", {
            variant: "success",
          });
        }
      } catch (error) {
        setFieldError(field, "Could not upload this file");
      } finally {
        setLoader({ state: false });
      }
    } else {
      return enqueueSnackbar("Invalid File Type", {
        variant: "error",
      });
    }
  }

  async function onFormSubmit(values) {
    const postData = JSON.parse(JSON.stringify(values));
    try {
      setLoader({ state: true, message: "Updating profile..." });
      const response = await AuthService.profileUpdate(postData);
      if (response.me) {
        enqueueSnackbar(response.message, {
          variant: "success",
        });
        window.location.reload();
        setModal(false);
      }
    } catch (error) {
      const message = getErrorMessage(error);
      enqueueSnackbar(message, {
        variant: "error",
      });
    } finally {
      setLoader({ state: false });
    }
  }

  return (
    <Drawer anchor={"right"} open={modal}>
      <DrawerContainer role="presentation">
        <HeadingContainer>
          <Heading>{labels["actions.edit_profile"]}</Heading>
          <CloseModal
            onClick={() => setModal(false)}
            src={require("../../../../assets/images/closeModal.svg")}
          />
        </HeadingContainer>
        <AgentFormContainer>
          <Formik
            initialValues={{
              houseNumber: userObject?.houseNumber || "",
              locality: userObject?.locality || "",
              city: userObject?.city || "",
              state: userObject?.state || "TN",
              pin: userObject?.pin || "",
              landmark: userObject?.landmark || "",
              avatarUrl: userObject?.avatarUrl || "",
              panNumber: userObject?.panNumber || "",
            }}
            enableReinitialize
            validateOnBlur
            validationSchema={EditProfileSchema}
            validateOnChange
            onSubmit={onFormSubmit}
          >
            {({
              values,
              handleChange,
              errors,
              handleSubmit,
              touched,
              setFieldValue,
              setFieldError,
              handleBlur,
            }) => (
              <>
                <ProfilePicContainer>
                  <ImageWrapper onClick={() => fileRef.current.click()}>
                    <ProfilePic
                      src={
                        values?.avatarUrl
                          ? values.avatarUrl
                          : require("../../../../assets/images/userPlaceholder.svg")
                      }
                    ></ProfilePic>
                    <PickerIcon
                      src={require("../../../../assets/images/imagePicker.svg")}
                    />
                  </ImageWrapper>
                </ProfilePicContainer>
                <BasicDetails>
                  <LabelValueContainer>
                    <Label>{labels["user.name"]}</Label>
                    <Value>{userObject?.name}</Value>
                  </LabelValueContainer>
                  <LabelValueContainer>
                    <Label>{labels["user.email"]}</Label>
                    <Value>{userObject?.email}</Value>
                  </LabelValueContainer>
                  <LabelValueContainer>
                    <Label>{labels["user.mobile"]}</Label>
                    <Value>{userObject?.mobile}</Value>
                  </LabelValueContainer>
                </BasicDetails>
                <Flex style={{ marginBottom: 20 }}>
                  <FormField
                    input={{
                      name: "panNumber",
                      label: labels["user.pan"],
                      type: "text",
                      placeholder: "Eg. ABCDE1234F",
                    }}
                    {...{
                      touched,
                      errors,
                      values,
                      handleChange,
                      handleBlur,
                    }}
                  />
                </Flex>
                <FormField
                  input={{
                    name: "houseNumber",
                    label: labels["user.house_label"],
                    type: "text",
                    placeholder: "Eg. Flat 2C, PKP Apartment",
                    required: true,
                  }}
                  {...{
                    touched,
                    errors,
                    values,
                    handleChange,
                    handleBlur,
                  }}
                />
                <Flex>
                  <HalfWidth>
                    <FormField
                      input={{
                        name: "locality",
                        label: labels["user.locality"],
                        type: "text",
                        placeholder: "Eg. Saket",
                        required: true,
                      }}
                      {...{
                        touched,
                        errors,
                        values,
                        handleChange,
                        handleBlur,
                      }}
                    />
                  </HalfWidth>
                  <HalfWidth>
                    <FormField
                      input={{
                        name: "city",
                        label: labels["user.city"],
                        type: "text",
                        placeholder: "Eg. Delhi",
                        required: true,
                      }}
                      {...{
                        touched,
                        errors,
                        values,
                        handleChange,
                        handleBlur,
                      }}
                    />
                  </HalfWidth>
                </Flex>
                <Flex>
                  <HalfWidth style={{ marginBottom: 10 }}>
                    <FormField
                      input={{
                        name: "state",
                        label: labels["user.state"],
