import React, { useState, useEffect } from "react";
import styled from "styled-components";
import theme from "../../../assets/theme";
import COLORS from "../../../assets/Colors";
import Drawer from "./RecordingDrawer";
import { Heading } from "../../../styles/component/style";
import CustomTable from "../../common/CustomTable/CustomTable";
import { makeStyles } from "@material-ui/core";
import Accordion from "@material-ui/core/ExpansionPanel";
import AccordionSummary from "@material-ui/core/ExpansionPanelSummary";
import AccordionDetails from "@material-ui/core/ExpansionPanelDetails";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import CaseService from "../../../services/CaseService";
import { getErrorMessage } from "../../../helpers/functions";
import { useSnackbar } from "notistack";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    backgroundColor: "#f9f9f9",
    border: "solid 0.5px #e1e3ee",
    boxShadow: "none",
    margin: "16px 0px 0px !important",
    "& .MuiIconButton-root": {
      padding: 0,
      marginRight: 15,
      backgroundColor: "#fff"
    },
    "& .MuiSvgIcon-root": {
      fill: "#00838c"
    }
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular
  }
}));

function TableWithAccordion({ MTRef, title, id }) {
  const classes = useStyles();
  const [data, setData] = useState();
  const { enqueueSnackbar } = useSnackbar();

  useEffect(() => {
    async function getViewMeeting() {
      try {
        const response_data = await CaseService.getMeetingVideo(id);
        console.log("name", response_data);
        if (response_data) {
          setData(response_data);
        }
      } catch (error) {
        const message = getErrorMessage(error);
        enqueueSnackbar(message, {
          variant: "error"
        });
      }
    }
    getViewMeeting();
  }, []);

  console.log("item", data?.recordings?.length);

  const columns = [
    {
      field: "recordingName",
      title: "RecordingName",
      sorting: false,
      render: (rowData) => <div>Recording {rowData?.tableData?.id + 1}</div>
    },
    {
      field: "RecordingUrl",
      title: "RecordingUrl",
      sorting: false,
      render: (rowData, index) => (
        <HyperLink onClick={() => window.open(rowData?.RecordingUrl)}>
          {/* Recording Link-{(rowData?.recordingName).replace(/\D/g, "")} */}
          Recording Link-{rowData?.tableData?.id + 1}
        </HyperLink>
      )
    },
    {
      field: "meetingID",
      title: "ID",
      sorting: false,
      render: (rowData) => <p>{rowData.meetingID}</p>
    },
    {
      field: "time",
      title: "created at",
      sorting: false,
      render: (rowData) => <p>{rowData.time}</p>
    }
  ];

  return (
    <Accordion className={classes.root}>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-controls="panel1a-content"
        id="panel1a-header"
      >
        <AccordionHeading style={{ marginLeft: 24 }}>{title}</AccordionHeading>
      </AccordionSummary>
      <AccordionDetails>
        <TableContainer>
          {data?.recordings?.length ? (
            <CustomTable
              pluralTitle="Recordings"
              singularTitle="Recordings"
              {...{
                columns
                // MTRef,
              }}
              // state={{
              //   data: data?.recordings
              // }}
              data={data?.recordings}
              noToolbar
              // hidePagination={data?.recordings?.lastPage === 1}
              // pageSize={data?.length ? data?.recordings?.length : 10}
              pageSize={1000}
              hidePagination={true}
            />
          ) : (
            <div className="no-result">No Recordings Found</div>
          )}
        </TableContainer>
      </AccordionDetails>
    </Accordion>
  );
}
export default TableWithAccordion;

export const Header = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 19px 40px;
  margin-top: 15px;
  border-bottom: 1px solid ${COLORS.BORDER_GREY};
`;
export const TableContainer = styled.div`
  /* margin: 30px 29px auto 38px; */
  width: 100%;
  & .no-result {
    font-family: ${theme.fonts.primaryFontSemiBold};
    font-size: 16px;
    color: ${COLORS.COLOR_DARK};
    text-align: center;
  }
`;
export const AccordionHeading = styled.div`
  font-family: ${theme.fonts.primaryFontSemiBold};
  font-size: 16px;
  color: ${COLORS.COLOR_DARK};
`;

export const AccordionContainer = styled.div`
  padding: 0 20px;
`;

export const ImageWrapper = styled.div`
  cursor: pointer;
`;
export const HyperLink = styled.div`
  font-family: ${theme.fonts.primaryFontSemiBold};
  font-size: 14px;
  color: ${COLORS.BTN_GREEN};
  text-decoration: underline;
  margin-right: ${({ marginRight }) => (marginRight ? marginRight : "0px")};
  cursor: pointer;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
`;

export const StyledTitle = styled.div`
  font-family: ${theme.fonts.primaryFontBold};
  width: 200px;
`;
