  return API({
    method: "POST",
    url: config.urls.case.case + `/${id}/negotiations`,
    data,
  });
};

const quitCase = (id, data) => {
  return API({
    method: "DELETE",
    url: config.urls.case.case + `/${id}/negotiations`,
    data,
  });
};

const caseRequests = (query) => {
  return API({
    method: "GET",
    url:
      config.urls.case.invites +
